import React from 'react';
import { useGlobalContext } from '../../context/GlobalState';

/**
 * AnalyticsDashboard summarises behaviour logs and AI predictions.  For
 * demonstration purposes, it shows counts of positive and negative logs.
 */
export default function AnalyticsDashboard() {
  const { state } = useGlobalContext();
  const logs = state.logs;

  const positiveCount = logs.filter((log) => log.type === 'positive').length;
  const negativeCount = logs.filter((log) => log.type === 'negative').length;

  return (
    <section className="p-6 bg-white rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Analytics Overview</h2>
      <div className="flex space-x-4">
        <div className="flex-1 p-4 bg-green-100 rounded">
          <p className="text-sm text-green-600">Positive Behaviours</p>
          <p className="text-2xl font-bold text-green-700">{positiveCount}</p>
        </div>
        <div className="flex-1 p-4 bg-red-100 rounded">
          <p className="text-sm text-red-600">Negative Behaviours</p>
          <p className="text-2xl font-bold text-red-700">{negativeCount}</p>
        </div>
      </div>
    </section>
  );
}
