import React, { useEffect } from 'react';
import { useGlobalContext } from '../../context/GlobalState';

/**
 * WeatherDashboard shows the Behaviour Weather System™ forecast.
 * It fetches predictions from the backend via the global context
 * and renders a concise view with risk levels and prevention strategies.
 */
export default function WeatherDashboard() {
  const { state, dispatch } = useGlobalContext();

  useEffect(() => {
    async function fetchPredictions() {
      try {
        const response = await fetch('/api/predictions', {
          headers: { Authorization: `Bearer ${state.token}` }
        });
        const data = await response.json();
        dispatch({ type: 'SET_PREDICTIONS', payload: data });
      } catch (err) {
        dispatch({ type: 'SET_ERROR', payload: 'Failed to load predictions' });
      }
    }
    fetchPredictions();
  }, [state.token, dispatch]);

  const forecast = state.predictions.forecast || {
    risk: 2,
    description: 'Sunny',
    criticalTimes: [],
    focusStudents: [],
    strategies: []
  };

  return (
    <section className="p-6 bg-primary-light rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Classroom Weather</h2>
      <p className="mb-2">Risk Level: <span className="font-bold">{forecast.risk}</span></p>
      <p className="mb-2">Weather: {forecast.description}</p>
      {forecast.criticalTimes.length > 0 && (
        <div className="mt-4">
          <h3 className="font-medium">Critical Times</h3>
          <ul className="list-disc list-inside text-sm">
            {forecast.criticalTimes.map((ct, idx) => (
              <li key={idx}>{ct}</li>
            ))}
          </ul>
        </div>
      )}
      {forecast.strategies.length > 0 && (
        <div className="mt-4">
          <h3 className="font-medium">Prevention Strategies</h3>
          <ul className="list-disc list-inside text-sm">
            {forecast.strategies.map((s, idx) => (
              <li key={idx}>{s}</li>
            ))}
          </ul>
        </div>
      )}
    </section>
  );
}
