import React from 'react';
import { useGlobalContext } from '../../context/GlobalState';

/**
 * ConstellationDashboard visualises relationships between students.  It
 * emphasises collaboration and highlights connections.  For a simple
 * implementation, we show a list of students and their interactions.
 */
export default function ConstellationDashboard() {
  const { state } = useGlobalContext();
  const logs = state.logs;

  // Build a mapping of student -> interactions with others
  const interactions = {};
  logs.forEach((log) => {
    const { student, peers = [] } = log;
    if (!interactions[student]) interactions[student] = new Set();
    peers.forEach((peer) => interactions[student].add(peer));
  });

  return (
    <section className="p-6 bg-accent-light rounded shadow">
      <h2 className="text-xl font-semibold mb-4">Student Constellation</h2>
      {Object.keys(interactions).length === 0 && <p>No interactions recorded.</p>}
      <ul className="space-y-2">
        {Object.entries(interactions).map(([student, peers]) => (
          <li key={student} className="p-3 bg-white rounded shadow">
            <p className="font-medium">{student}</p>
            <p className="text-sm text-gray-600">Peers: {[...peers].join(', ') || 'None'}</p>
          </li>
        ))}
      </ul>
    </section>
  );
}
