import React from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import { useStore } from "./state/useStore";
import { useGlobalContext } from './context/GlobalState';
import WeatherDashboard from "./features/weather";
import GardenDashboard from "./features/garden";
import ConstellationDashboard from "./features/constellation";
import AnalyticsView from "./features/analytics";
import QuickLog from "./features/quicklog";
import Login from './features/Auth/Login';

/**
 * Root application component.  Defines the top navigation bar and routes to
 * each feature.  QuickLog is accessible from the nav and via its own
 * route to meet the ≤2 click requirement.  The app also checks for
 * authentication state and redirects to login if necessary (not
 * implemented yet).
 */
export default function App() {
  const { currentView, setView } = useStore();
  // For now, we'll keep the old auth logic until we migrate it to Zustand
  const { state } = useGlobalContext();
  const isAuthenticated = !!state.token;
  return (
    <div className="min-h-screen flex flex-col">
      {/* Navigation bar shows only when authenticated */}
      {isAuthenticated && (
        <nav className="bg-primary-dark text-white p-4 flex justify-between items-center">
          <div className="flex space-x-4">
            <Link to="/weather" className="hover:underline">Weather</Link>
            <Link to="/garden" className="hover:underline">Garden</Link>
            <Link to="/constellation" className="hover:underline">Constellation</Link>
            <Link to="/analytics" className="hover:underline">Analytics</Link>
          </div>
          <div>
            <Link to="/quick-log" className="bg-secondary-dark text-white px-3 py-1 rounded hover:bg-secondary">Quick‑Log</Link>
          </div>
        </nav>
      )}
      <main className="flex-1 p-4 bg-primary-light">
        <Routes>
          {!isAuthenticated && (
            <>
              <Route path="/login" element={<Login />} />
              <Route path="*" element={<Login />} />
            </>
          )}
          {isAuthenticated && (
            <>
              <Route path="/weather" element={<WeatherDashboard />} />
              <Route path="/garden" element={<GardenDashboard />} />
              <Route path="/constellation" element={<ConstellationDashboard />} />
              <Route path="/analytics" element={<AnalyticsDashboard />} />
              <Route path="/quick-log" element={<QuickLog />} />
              <Route path="/" element={<Navigate to="/weather" replace />} />
            </>
          )}
        </Routes>
      </main>
    </div>
  );
}
